<?php
namespace NetAuth; class RestrictPassword { public function __construct() { add_filter('show_password_fields', array($this, 'disable')); add_filter('allow_password_reset', array($this, 'disable')); add_filter('gettext', array($this, 'remove')); } public function disable() { if (is_admin()) { $sp9b4073 = wp_get_current_user(); $spffcd2d = new \WP_User($sp9b4073->ID); return !empty($spffcd2d->roles) && is_array($spffcd2d->roles) && array_shift($spffcd2d->roles) == 'administrator'; } return false; } public function remove($sp55070e) { return preg_replace('/lost your password\\??/is', '', $sp55070e); } }